insert into classe_permet (id_classe, competence, permet, new) values ((select id from classe where nom = 'paladin'), 'maitrise_bouclier', 200, 'no'),((select id from classe where nom = 'Guerrier'), 'maitrise_bouclier', 100, 'yes'),((select id from classe where nom = 'Champion'), 'maitrise_bouclier', 100, 'no');
insert into comp_perso (id_comp, competence, valeur, id_perso) select 1 as id_comp, 'maitrise_bouclier' as 'competence', 1 as 'valeur', perso.id as 'id_perso' from perso where classe in (select classe.nom from classe_permet,classe where classe_permet.competence = 'maitrise_bouclier' and classe_permet.id_classe = classe.id);
