-- Recettes de forge
CREATE TABLE IF NOT EXISTS `forge_recette` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nom` varchar(50) NOT NULL,
  `objet` varchar(5) NOT NULL,
  `difficulte` int(11) NOT NULL,
  `type_bonus` varchar(25) NOT NULL,
  `effet_bonus` int(11) NOT NULL,
  `type_malus` varchar(25) NOT NULL,
  `effet_malus` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;


-- Ingrédients des recettes de forge
CREATE TABLE IF NOT EXISTS `forge_recette_ingredient` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `id_recette` int(10) unsigned NOT NULL,
  `id_ingredient` int(10) unsigned NOT NULL,
  `nombre` tinyint(3) unsigned NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;


