
-- Table `quete`
DROP TABLE IF EXISTS `quete`;
CREATE TABLE IF NOT EXISTS `quete` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `nom` varchar(100) NOT NULL,
  `fournisseur` enum('bureau_quete','ecole_combat','taverne','alchimiste','université','bar','royaume','special') NOT NULL DEFAULT 'special',
  `type` enum('individuel','groupe','royaume') NOT NULL DEFAULT 'individuel',
  `repetable` enum('oui','non') NOT NULL DEFAULT 'non',
  `royaume` varchar(100) NOT NULL,
  `requis` text NOT NULL,
  `star_royaume` int(10) unsigned NOT NULL DEFAULT '0',
  `nombre_etape` int(10) unsigned NOT NULL DEFAULT '1',
  `terrain` varchar(10) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=151 ;



-- Table `quete_etape`

DROP TABLE IF EXISTS `quete_etape`;
CREATE TABLE IF NOT EXISTS `quete_etape` (
  `id` int(100) NOT NULL AUTO_INCREMENT,
  `id_quete` int(10) NOT NULL,
  `etape` int(10) NOT NULL DEFAULT '1',
  `variante` int(11) DEFAULT '0',
  `titre` varchar(50) NOT NULL,
  `description` text NOT NULL,
  `niveau` int(10) NOT NULL DEFAULT '1',
  `objectif` text NOT NULL,
  `collaboration` enum('aucune','groupe','royaume') NOT NULL DEFAULT 'groupe',
  `requis` text NOT NULL,
  `gain_perso` text NOT NULL,
  `gain_groupe` text NOT NULL,
  `gain_royaume` text NOT NULL,
  `init` text NOT NULL,
  PRIMARY KEY (`id`),
  KEY `id` (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=142 ;

--
-- Contenu de la table `quete_etape`
--



-- Table `quete_perso`
DROP TABLE IF EXISTS `quete_perso`;
CREATE TABLE `quete_perso` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `id_perso` int(10) unsigned NOT NULL,
  `id_quete` int(10) unsigned NOT NULL,
  `id_etape` int(10) unsigned NOT NULL,
  `avancement` text CHARACTER SET utf8 NOT NULL,
  PRIMARY KEY (`id`),
  KEY `perso_quete` (`id_perso`,`id_quete`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=8 ;

-- PNJs
UPDATE pnj SET texte = '[isquete:53-e2][id:9]Je reviens juste des ruines de Myriandre princesse. vous aviez raison, il se passe quelque chose d''étrange là bas. Je me suis fait attaqué par un squelette, et j''ai pu découvrir un journal qui a appartenu à un mage humain. tenez...[/id:9]\n[/isquete:53-e2]\n[non_quete:53]<i>Vous trouvez la jeune fille en pleur, le regard perdue dans le vide, comme tétanisée </i>\n[ID:1]Que se passe t''il princesse ? ... princesse ?[/ID:1][/non_quete:53]\n*****\n<i>La jeune femme lève lentement le regard sur vous </i>\n- Le chaos, la mort, elles arrivent...\n<i>Elle regarde soudain autour d''elle comme si elle prenait soudainement conscience de l''endroit où elle se trouve </i>\n[ID:2]Que vous est il arrivé princesse ? Une vision ?[/ID:2]\n*****\n- Une vision si terrible que je ne puis l''exprimer par des mots...\n[ID:3]Puis je vous aider en quoi que ce soit ?[/ID:3]\n*****\n- Je ne pourrais oser vous demander de prendre un si grand risque, ma vision était des plus flous, comme embrumée, mais j''ai sentie un pouvoir terrifiant et un immense danger pour nous tous.\n[ID:4]J''aimerais vous aider princesse, mais je ne me sens pas assez fort pour vaincre un tel danger.[/ID:4]\n[ID:5]Bah, peu m''importe, cela n''a aucune rapport avec moi[/ID:5]\n[ID:6]Si notre peuple est en danger, il est de mon devoir de faire tout ce qui en mon pouvoir pour le protéger.[/ID:6]\n*****\n- Vous envoyer sur cette mission serait vous envoyer à la mort, ne vous inquiétez pas, je trouverais quelqu''un de plus expérimenté.\n[retour]\n*****\n- Vous n''êtes pas digne de cette mission de toute façon... partez maintenant.\n[retour]\n*****\n- Je m''en veux de vous envoyer sur une mission qui pourrait se réveler mortelle, mais votre courage oblige mon respect.\nEcoute, j''ai pu reconnaitre les ruines de la ville humaine de Myriandre, qui se trouve au centre des royaumes, juste au nord d''Ereth drannyr, la capitale vampire.\nElle fut détruite il y a quelques temps de cela par le dragon fou Arderax.\nJ''ai des raisons de penser qu''il y a quel que chose de plus dérrière tout ça, tu dois te rendre la bas et mener ton enquète avec la plus grande discétion, note toute chose qui sort de l''ordinaire et reviens me voir.\nAu moindre danger, n''hésite pas a faire marche arrière, il y a des aventuriers héroïques, et des aventuriers vivants, rarement les deux...\n\n[ID:7]Heu finalement cela a l''air beaucoup trop dangereux, je préfere ne pas y aller, j''en suis désolé[/ID:7]\n[ID:8]Je vous entend princesse, et il en sera fait comme vous le souhaitez.[/ID:8]\n*****\n- Ce n''est pas grave, je comprend, c''est effectivement très dangereux. Ne t''inquiète pas je trouverais quelqu''un d''autre.\n[retour]\n*****\n[prendquete:53]\nMerci de votre aide !\n[retour]\n*****\n< la jeune femme saisie le journal et se met a rapidement feuilleter les pages >\n- c''est bien ce que je craignais, il y a plus derrière la destruction de Myriandre que la simple vengeance d''un dragon fou...\n[ID:10]- Qu''est ce qu''on peut faire pour en apprendre plus ?[/ID:10]\n*****\n- Hum, et bien il y a un Elfe des bois, Dryasaïde est son nom, c''est un sage parmi les siens et un vieil ami de mon père. Si tu va le voir il saura te dire quoi faire.\n[ID:11]- Ou exactement pourrais je le trouver ?[/ID:11]\n*****\n- Et bien il vit plutôt en ermite, mais si tu parcours la forêt qui entoure le royaume de Loch''jaden, tu devrais parvenir à le trouver.\n[ID:12]- Très bien princesse, je le trouverais et découvrirais ce qui se trame.[/ID:12]\n*****\n[quete]\n[retour]' WHERE id = 1;
UPDATE `pnj` SET `texte` = 'Que veux tu novice ?\r\n\r\n[isquete:53-e2][id:1]Heuh... j''ai trouvé un journal, et je me demandais si, dans votre grande sagesse, vous ouviez m''éclairer.[/isquete:53-e2][non_quete:53-e2]Qui je suis ?\r\nComme si un simple nom suffisait pour t''en dire plus sur moi... enfin...\r\nJe me nomme Argon, Archimage au service de sa majesté, je suis un adepte de l''Art. J''altère la réalité, et je la plie à ma volonté, tels sont les pouvoirs que j''ai acquis au cours de plusieurs décennies de dur labeur.\r\n[retour][/non_quete:53-e2][quete_finie:53-e2]Qui je suis ?\r\nComme si un simple nom suffisait pour t''en dire plus sur moi... enfin...\r\nJe me nomme Argon, Archimage au service de sa majesté, je suis un adepte de l''Art. J''altère la réalité, et je la plie à ma volonté, tels sont les pouvoirs que j''ai acquis au cours de plusieurs décennies de dur labeur.\r\n[retour][/quete_finie:53-e2]\r\n*****\r\nCeci me semble inquiétant. Mais je ne puis t''aider.\r\nVas voir Dryasaïde, c''est un confrère.\r\nIl saura certainement te dire quoi faire.\r\n[id:2]- Ou exactement pourrais je le trouver ?[/id:2]\r\n*****\r\nC''est un Elfe des Bois, mais il vit en ermite.\r\nParcours la forêt qui entoure le royaume de Loch''jaden, tu devrais parvenir à le trouver.\r\n[quete]\r\n[retour]' WHERE `pnj`.`id` = 2;
UPDATE `pnj` SET `texte` = '[non_quete:53-e2]Le chaos, mon jeune ami... le chaos, c''est la notion la plus importante de l''univers.\r\nPourquoi un guerrier qui a remporté mille batailles finit il un jour percé par la flèche d''un simple gobelin ? la malchance diront certains.\r\nLe Chaos te dira un Vörsh, car telle est la nature des choses : changeante. l''ordre est une illusion figée, les certitudes ton pire ennemi, c''est une vérité que tu devras accepter vite si tu veux survivre.\r\n[retour][/non_quete:53-e2][quete_finie:53-e2]Le chaos, mon jeune ami... le chaos, c''est la notion la plus importante de l''univers.\r\nPourquoi un guerrier qui a remporté mille batailles finit il un jour percé par la flèche d''un simple gobelin ? la malchance diront certains.\r\nLe Chaos te dira un Vörsh, car telle est la nature des choses : changeante. l''ordre est une illusion figée, les certitudes ton pire ennemi, c''est une vérité que tu devras accepter vite si tu veux survivre.\r\n[retour][/quete_finie:53-e2]\r\n[isquete:53-e2]Le chaos, mon jeune ami... le chaos, c''est la notion la plus importante de l''univers.\r\n\r\n[id:1]En parlant de chaos, j''ai trouvé un journal dont les propos sont plutôt chaotique...[/id:1]\r\n*****\r\nEt alors que veux-tu que j''y fasse ?\r\n\r\n[id:2]Je me demandais si vous pouviez m''aider à le décrypter[/id:2]\r\n*****\r\nEst-ce que j''ai l''air d''un de ces rats de bibliothèque tout chétifs et qui ne sortent jamais de leur tour obscure ?\r\n\r\nMais comme tu bas une bonne tête je vais te donner un conseil : dans la forêt du royaume de Loch''jaden vis un sage Elfe des Bois, Dryasaïde est son nom. C''est un drôle de loustic mais il saura sans doute t''aider.' WHERE `pnj`.`id` = 3;
UPDATE `pnj` SET `texte` = 'Rhaaaaaaa\r\n<i>L''orc fait tournoyer une énorme hache</i>\r\nQu''est ce que tu veux !!!\r\n\r\n[non_quete:53-e2][ID:1]- Heu .... rien ...[/ID:1][/non_quete:53-e2]\r\n[quete_finie:53-e2][ID:1]- Heu .... rien ...[/ID:1][/quete_finie:53-e2]\r\n[isquete:53-e2][id:3]- Heu .... je me demandais si vous pouviez m''aider.[/id:3][/isquete:53-e2]\r\n*****\r\nRIEN, COMMENT CA RIEN ?\r\nTu me déranges pour rien ???\r\nTu vois cette hache, elle a appartenu a mon père, et a mon grand père avant lui, et il est dit qu''un jour elle servira a terrasser un dragon !! mais peut être qu''avant je vais la tremper dans ta colonne vertébrale pour aiguiser sa lame !!\r\n\r\n[ID:2]- Désolé de vous avoir importuné, je m''en vais[/ID:2]\r\n*****\r\nOue c''est ça dégage vermiceau... tu as de la chance que je sois dans un bon jour sinon je t''aurais déja éparpillé.\r\n[retour]\r\n*****\r\nT''aider ?\r\nEt pourquoi je t''aiderai ?\r\n\r\n[id:4]J''ai trouvé ce journal et ...[/id:4]\r\n*****\r\nUn journal ?!\r\nPour qui me prends tu ?\r\nJe ne suis un des ces faibles qui passe leur temps à réfléchir et ne savent rien faire de leur bras, comme cet ermite qui vis dans la forêt.\r\n\r\n[id:5]Un ermite ?[/id:5]\r\n*****\r\nDryasaïde, un Elfe des Bois encore plus bizarre que les autres, il vit près de Keleth.  Maintenant fout moi la paix ou je me ferais un plaisir d''aiguiser ma hache avec ta colonne vertébrale.\r\n\r\n[quete]\r\n[retour]' WHERE `pnj`.`id` = 4;
UPDATE `pnj` SET `texte` = '[isquete:53-e2][id:10]- Me voici vieux maître, je suis de retour des ruines de la ville humaine. il semble que ce soit là en effet que se trouve l''origine du sort. tenez j''y ai découvert ce journal sur le cadavre d''un squelette qui m''a attaqué.[/id:10]\r\n[/isquete:53-e2][non_quete:53]<i>Le vieux skaven semble songeur, plongé dans ses pensées tandis qu''il tise patiemment sur sa vieille pipe </i>\r\n[ID:1]Tout va bien vieux maître ?[/ID:1][/non_quete:53]\r\n*****\r\n- Hein, ha, non tout ne va pas bien, loin de là\r\n[ID:2]Que se passe-t il ?[/ID:2]\r\n*****\r\nHum, des fluctuations, voila ce qui se passe\r\n[ID:3]Heu, des fluctuations, voila, oui... ?[/ID:3]\r\n*****\r\nLes bases de la magie jeune novice ! Quand on lance un sort, on crée une perturbation dans le flux de mana, cet écho crée une résonance dans l''espace astral\r\n[ID:4]des fluctuations ![/ID:4]\r\n*****\r\nExactement.\r\n[ID:5]Et quel est le problème ici ?[/ID:5]\r\n*****\r\nLe problème, c''est que depuis quelques temps, je sens une résonance inhabituelle dans le champ astral... une résonance persistante, comme si un sort très puissant était maintenu.\r\n[ID:6]Vous pouvez déterminer quel type de sort[/ID:6]\r\n*****\r\nPas vraiment... c''est ce qui est inquiétant. La résonance me parvient comme un vague écho, ce qui semble indiquer que le sort a été lancé de très loin. Et pourtant, rien que le fait que cela me parvienne jusqu''ici est un indice de sa puissance... cela fait quelques temps que cela dure, et le phénomène va crescendo.\r\n[ID:7]Vous avez une explication ?[/ID:7]\r\n*****\r\nEh bien soit le sort prend de la puissance avec le temps... soit, et c''est plus probable, la puissance du sortilège a été camouflée et c''est le sort de camouflage qui perd de sa puissance, révélant petit à petit la puissance du vrai sortilège.\r\n[ID:8]Je vois, puis-je vous aider à dévoiler ce mystère ?[/ID:8]\r\n*****\r\nEh bien si tu le souhaites, oui, j''ai réussi à déterminer approximativement la source du sort, je l''ai localisé à proximité de l''ancienne ville humaine de Myriandre. Rends-toi là-bas, et essaye de localiser d''où pourrait provenir cette perturbation du champ magique.\r\n[ID:9]Très bien vieux maître, je viendrai vous faire mon rapport.[/ID:9]\r\n*****\r\nBonne journée !\r\n[prendquete:53]\r\n[retour]\r\n*****\r\n< Le vieux sorcier prend le journal en main et feuillette lentement les pages une à une. Une fois cela terminé, il reste pensif un moment. > \r\n- Un sort d''illusion camouflé...\r\n[ID:11]- Oui, cela ne nous apprend pas grand chose de neuf n''est ce pas?[/ID:11]\r\n*****\r\n- Détrompe toi, il y a beaucoup à apprendre dans ce livre pour celui qui sais voir...\r\n[ID:12]- comment ça ?[/ID:12]\r\n*****\r\n- Ha les jeunes, vous entendez mais vous n''écoutez pas, vous voyez, mais vous ne comprenez pas... enfin... il y a un vieux confrère à moi, un elfe des bois, Dryasaïde, il vit dans la forêt au nord-est de Keleth . si tu veux avoir une chance de voir ce qui se cache derrière le sortilège, tu devras aller le trouver et suivre ses conseils...\r\n[ID:13]- J''imagine que vous ne m''en direz pas plus ?[/ID:13]\r\n*****\r\n- Ha, enfin une remarque emprunte de sagesse\r\n< le vieux skaven sourit > \r\n[quete]\r\n[retour]' WHERE `pnj`.`id` = 5;
UPDATE `pnj` SET `texte` = '[isquete:53-e2][id:6]Tenez j''ai découvert ce journal.[/id:6][/isquete:53-e2]\r\n[non_quete:53]<i>L''ombre semble quel que peu perturbée, son regard, songeur, se perd dans le vide. Intrigué, vous vous approchez.</i>\r\n[ID:1]Quelque chose ne va pas ?[/ID:1][/non_quete:53]\r\n*****\r\nHumm... des questions qui appellent des réponses, qui appellent d''autres questions...\r\n[ID:2]Hein ?[/ID:2]\r\n*****\r\n<i>Le spectre lève les yeux sur vous, comme si il venait seulement de vous apercevoir</i>\r\nHa c''est vous, il se passe des choses, des choses qui demandent des confirmations, j''ai comme une sensation étrange, familière... se pourrait il que... hum\r\n[ID:3]Si vous me disiez ce qui se passe, peut être pourrais vous être utile en quoi que ce soit ? [/ID:3]\r\n*****\r\nHa, méfie toi, tu pourrais être exaucé bien au delà de tes désirs... mais oui tu peux peut être m''être utile, si tu t''en sens le courage novice !\r\n[ID:4]Je suis courageux.[/ID:4]\r\n*****\r\nMaintenant oui peut être... tu est si candide... enfin, peut être est ce que cela suffira, je ne peux pas me permettre de faire la fine bouche. Il y a une cité au coeur du continent, une cité ravagée, qui se nomme Myriandre... Tu dois t''y rendre, une fois la bas tu parcouras ses ruines avec la plus grande prudence et tu me rapporteras tout ce qui peux te paraître sortir de l''ordinaire.\r\n[ID:5]J''y vais et je me promène... et c''est tout ?[/ID:5]\r\n*****\r\nNous verrons bien si tu est à la hauteur de cette tache déjà... mais méfie toi, si mes craintes se trouvent confirmées, il se pourrait que ta mission soit bien plus périlleuse qu''il n''y parait... va maintenant, et essaye de revenir en un seul morceau.\r\n[quete:53]\r\n*****\r\n<i>le spectre passe sa main sur le journal, une faible lueur s''en dégage soudain. Anredür prend un air songeur </i>\r\n[ID:7]Est ce que cela confirme ce que vous redoutiez ?[/ID:7]\r\n*****\r\nJ''en ai peur oui.\r\n[ID:8]Quelle sera la suite des évènements ? \r\n[/ID:8]\r\n*****\r\nHa, te voila bien pressé de courir vers ta mort... enfin ! Il y a un mage elfe qui vis au nord-est de la forêt qui entoure le royaume des elfes des bois. Tu dois aller le voir, il connaît notre affaire et pourra sûrement t''aider si tu lui en parle.\r\n[ID:9]Très bien j''irais voir cet elfe...[/ID:9]\r\n*****\r\nEt hum soit prudent,il se peut qu''il soit devenu un peu susceptible avec l''âge.\r\n[quete]' WHERE `pnj`.`id` = 6;
UPDATE `pnj` SET `texte` = '[non_quete:53-e2][ID:1]- ...[/ID:1][/non_quete:53-e2]\r\n[quete_finie:53-e2][ID:1]- ...[/ID:1][/quete_finie:53-e2]\r\n[isquete:53-e2][id:2]- Bonjour... puis-je vous poser une... question ?[/id:2][/isquete:53-e2]\r\n*****\r\nNe sois pas effrayé, je me nomme Torkuz, autrefois un puissant guerrier, puissant... et vaniteux...\r\nJ''ai cru pouvoir me dresser face à la puissance des dieux. Autrefois j''ai cru pouvoir me mesurer à Cemiros lui même durant une grande bataille... il m''a brisé sans même y prêter attention, détruisant mon corps, pulvérisant mon âme même. Mais dans sa grande mansuétude, le changeur m''a offert une nouvelle chance de le servir, et aujourd’hui j’attends mon heure...\r\n[retour]\r\n*****\r\n- Bien sûr, ne sois pas effrayé.\r\nJe me nomme Torkuz.\r\n\r\n[id:3]- J''ai trouvé ce journal dans les ruines de Myriandre, et je me demandais si vous pouviez m''aider à le décrypter[/id:3]\r\n*****\r\n- Fais voir... hum... il semble qu''une puissante et inquiétante magie soit à l''œuvre.\r\nMalheureusement ce n''est pas mon domaine. Mais je connais quelqu''un qui pourrait t''aider.\r\nIl s''appelle Dryasaïde, c''est un ermite qui vit dans les bois de Loch''jaden.\r\n[quete]\r\n[retour]' WHERE `pnj`.`id` = 7;
UPDATE `pnj` SET `texte` = '[non_quete:53-e2]La Hache et le sang ! voila le sens de la vie.\r\nLe combat est partout, des guerres, il y en a toujours eu, il y en aura toujours. Mon royaume a autrefois été très puissant, puis les miens ont été réduit en esclavage, aujourdhui nous retrouvons notre forçe d''antan, les choses changent, un cycle, voila ce que c''est, aujourdhui je vis, demain je meurt.\r\nGrumf, voila pourquoi je goute chaque moment, voila pourquoi je vis chaque combat... car chacun d''entre eux pourrait être le dernier !\r\n[retour][/non_quete:53-e2][quete_finie:53-e2]La Hache et le sang ! voila le sens de la vie.\r\nLe combat est partout, des guerres, il y en a toujours eu, il y en aura toujours. Mon royaume a autrefois été très puissant, puis les miens ont été réduit en esclavage, aujourdhui nous retrouvons notre forçe d''antan, les choses changent, un cycle, voila ce que c''est, aujourdhui je vis, demain je meurt.\r\nGrumf, voila pourquoi je goute chaque moment, voila pourquoi je vis chaque combat... car chacun d''entre eux pourrait être le dernier !\r\n[retour][/quete_finie:53-e2][isquete:53-e2]La Hache et le sang ! voila le sens de la vie.\r\n\r\n[id:1]-Euh… oui… sans doute…[/isquete:53-e2]\r\n*****\r\nLe combat est partout, des guerres, il y en a toujours eu, il y en aura toujours. \r\nMais je suppose que ce n''est pas pour ça que tu vient me voir ?\r\n\r\n[id:2]En effet non. Je suis venu pour chercher de l''aide à propos d''un journal que j''ai trouvé. J''ai du combattre un squelette pour ça[/id:2]\r\n*****\r\nComme je le disais, le combat est partout.\r\nMais je ne vois pas en quoi je pourrais t''aider, c''est plus un sage que tu devais aller voir.\r\n\r\n[id:3]Et savez-vous où je pourrais en trouver un ?[/id:3]\r\n*****\r\nIl me semble bien avoir entendu parler d''un ermite qui vit dans la forêt de Loch''jaden. Un Elfe des Bois… attends… Dryasaïde… oui c''est ça, c''est son nom.\r\nIl pourra certainement t''en dire plus.\r\n[quete]\r\n[retour]' WHERE `pnj`.`id` = 8;
UPDATE `pnj` SET `texte` = 'Ne discute jamais avec un elfe, ne marchande jamais avec un nain, ne te bat jamais avec un troll, ne contrarie jamais un barbare, ne joue jamais aux cartes avec un humain, ne discute jamais théologie avec un skaven,ne tourne jamais le dos à un Vörsh et surtout, surtout, ne fais jamais confiance à un dragon...\r\n\r\n[non_quete:53-e2][retour][/non_quete:53-e2][quete_finie:53-e2][retour][/quete_finie:53-e2][isquete:53-e2][id:1]- Merci du conseil, mais je venais vous parler d''un journal.[id:1][/isquete:53-e2]\r\n*****\r\n- Un journal ?\r\n\r\n[id:2]- Oui, j''ai trouvé ce journal dans les ruines de Myriandre. Je me demandais si vous pouviez m''aider[/id:2]\r\n*****\r\n[i]Alkor regarde le journal que vous lui tendez et fait la moue[/i]\r\n- Je ne pourrais pas vous t''aider là dessus. Il faudrait que tu ailles voir un sage. Il y en a un, Dryasaïde, un ermite Elfe des Bois qui vit dans la forêt de Loch''jaden. Il pourra probablement t''aider.\r\n\r\n[id:3]Je croyais qu''il ne fallait pas parler à un Elfe ?[/id:3]\r\n*****\r\nEt ne pas contrarier un Barbare !\r\n\r\n[id:4]Euh… oui c''est vrai, je vais vous laisser[id:4]\r\n*****\r\n[quete]\r\n[retour]' WHERE `pnj`.`id` = 9;
UPDATE `pnj` SET `texte` = '[isquete:53-e2][id:8]- Je me suis rendu sur les ruines de Myriandre et j''ai effectivement noté des évènements étranges... et le fait que j''ai été attaqué par un squelette n''est pas le moindre d''entre eux... j''ai d''ailleurs aussi découvert ce journal, tenez....[/id:8][isquete:53-e2]\r\n[non_quete:53]<i>L''elfe des bois vous interpelle </i>\r\nHey toi là, oui toi, viens là !\r\n<i> Le guerrier semble vous observer de la tête aux pieds </i>\r\nOué bon, j''imagine qu''il faudra bien que cela fasse l''affaire... j''ai besoin de quelqu''un pour une mission importante... quelqu''un de rapide et de discret, c''est une mission de reconnaissance.\r\n[ID:1]Heu non désolé ça ne m''intéresse pas.[/ID:1]\r\n[ID:2]Ah et en quoi consisterait cette mission ?[/ID:2][/non_quete:53]\r\n*****\r\n[retour]\r\n*****\r\nTu a certainement du apprendre les ravages que le dragon Arderax a causé à la cité humaine de Myriandre. On dit qu''il l''aurait complètement ravagée...\r\n[ID:3]Heu vaguement... et donc ?[/ID:3]\r\n*****\r\nEt bien il se trouve que je connaissais quelques un des guerriers qui étaient chargés de la défense de la ville. De bon stratèges... et une ville de cette importance contenait aussi forcément de puissants magiciens. Je connais la puissance des dragons, qui n''a d''égale que leur arrogance, mais je ne saurais croire qu''un dragon seul est parvenu a vaincre les défenses entières d''une ville aussi importante.\r\n[ID:4]Je comprend ce que vous voulez dire, et que voulez vous que je fasse ?[/ID:4]\r\n*****\r\nJe cherche quelqu''un qui puisse se rendre sur les ruines de la cité, afin de noter tout ce qui peux s''y passer d''anormal, et le cas échéant, essayer de découvrir ce qui a pu se produire durant cette attaque. J''ai besoin de quelqu''un qui sache se servir aussi bien de ses muscles que de son cerveau...\r\n[ID:5]Hum, ça m''a l''air assez dangereux, je préfère rester ici ![/ID:5]\r\n[ID:6]Hum, très bien je suis votre homme, j''irais enquêter sur les ruines de Myriandre, cela m''intrigue.[/ID:6]\r\n*****\r\n[retour]\r\n*****\r\n[ID:7]Très bien, peux être fera on quelque chose de vous finalement, vous savez ou se trouve Myriandre n''est ce pas ? les ruines se trouvent au centre des royaumes, entre les grandes rivières.[/ID:7]\r\n*****\r\nAu revoir !\r\n[prendquete:53]\r\n*****\r\n< L''elfe jette un rapide coup d''oeil au journal >\r\n- Grr un sort d''illusion, il ne manquait plus que ça, et il a l''air plutôt coton en plus...\r\n< l''elfe réfléchit un moment >\r\n- Attend, il y a bien ce vieil Ermite qui vit dans la forêt... son nom m''échappe, dyadis, dryasis ou un truc du style... toujours est il que c''est un mage spécialisé dans les sortilèges de ce type. Si tu arrives à mettre la main dessus, peut être pourra il nous en apprendre plus long sur cette histoire.\r\n[ID:9]- Dans la forêt vous dites ? bien ça ne devrait pas être trop dur de mettre la main sur lui.[/ID:9]\r\n*****\r\n[quete]\r\n[retour]' WHERE `pnj`.`id` = 10;
UPDATE `pnj` SET `texte` = '[isquete:53-e2][id:6]- Je suis de retour des ruines de Myriandre. Aucune trace la bas de l''unité aigle mais j''ai pu retrouver ce journal qui a appartenu à un de leur membre, lisez le, vous verrez, cela nous en dis long...[/id:6][isquete:53-e2][non_quete:53][ID:1]Je suis passé à l''université maître, et j''ai lu le journal de frankriss Hawkeye... Que signifie tout ceci ?[/ID:1][/non_quete:53]\r\n*****\r\nFrankriss est... ou plutôt était... un membre de l''unité Aigle. c''est une unité chargée de certaines missions de reconnaissance pour le service de renseignements de sa majesté. Il était en mission à Myriandre après l''assaut du dragon Arderax.\r\n[ID:2]Myriandre ?[/ID:2]\r\n*****\r\nMyriandre est une cité humaine. une cité état, indépendante du royaume de Lordaeris, une ville marchande assez importante... elle a été attaquée par le dragon fou Arderax qui l''a tout simplement ravagée sans qu''on puisse faire quoi que ce soit. la ville a dit-on été entièrement détruite... il est d''usage dans ce genre de situation d''envoyer une unité d''éclaireurs afin de déterminer s''il y a lieu d''envoyer de l''aide, ou a défaut évaluer l''impact stratégique sur la région de ce genre de situation...\r\n[ID:3]Et que c''est il passé ?[/ID:3]\r\n*****\r\nC''est la le mystère. Un groupe de pillards a récupéré le journal de hawkeye au bord des ruines, aucune trace de l''unité aigle ou même de corps. les pillards se sont dis qu''ils pourraient en tirer un bon profit en venant nous le vendre.\r\n[ID:4]Je vois... en quoi puis-je vous aider ? [/ID:4]\r\n*****\r\nL''unité aigle a disparue, c''est un groupe aguerri, et ce qui en est venu a bout pose un danger potentiel au royaume que nous devons absolument déterminer.\r\nLà ou un groupe entier à échoué, peut être qu''un individu pourra réussir. La discrétion peut prévaloir là ou la force armée est inutile. je veux que vous vous rendiez sur les ruines de la cité de Myriandre, qui se trouve au centre des royaumes, et que arriviez à déterminer ce qui s''y passe, et si possible ce qui est arrivé à l''unité aigle.\r\n[ID:5]Je comprends, le temps de me préparer et je me met en route.[/ID:5]\r\n*****\r\nBonne route !\r\n[prendquete:53]\r\n*****\r\n< Le vieux nain saisit le journal et lit patiemment chaque page, une fois qu''il a finit, il pousse un soupir triste >.\r\n- C''est terrible, il y a fort a parier que l''unité aigle a périt... il se passe à Myriandre des choses fortes étranges sous le couvert d''un sortilège de protection apparemment... Hum, laisse moi réfléchir... Il y a un vieux mage elfe, il vit dans la forêt qui entoure Keleth, la capitale des elfes des bois. On dit qu''il est spécialisé dans les sortilèges d''illusion... Si quelqu''un est a même de t''aider a percer ce sort, c''est bien lui, tu dois aller le trouver et tenter de percer ce mystère... nous devons bien ça à l''unité aigle.\r\n[quete]\r\n[retour]' WHERE `pnj`.`id` = 11;
UPDATE `pnj` SET `texte` = '[ISQUETE:53-e3]Ha, encore un... ne me dis rien, je sais déjà pourquoi tu es là... tu n''es pas le premier...\r\n[ID:1]Vous allez pouvoir m''aider alors ?[/ID:1]\r\n[/ISQUETE:53-e3][isquete:53-e4]Tiens, encore en vie ?[/isquete:53-e4][isquete:53-e5][non_quete:53]Tiens, encore en vie ?\r\n\r\n[id:4]J''ai tué tous les ennemis à mon royaume[/id:4][/isquete:53-e5][non_quete:53-e3]Qu''est-ce que tu viens faire là toi ?\r\nOn ne peut pas être tranquille ![/non_quete:53-e3]\r\n*****\r\nT''aider ? pourquoi est ce que je t''aiderais... Qu''est ce qui te fais seulement croire que tu es capable de survivre à ce qui se cache derrière ce sortilège ?\r\n[ID:2]- Heu... je...[/ID:2]\r\n*****\r\nBlablabla tu ne sais rien...Si veux aller plus loin, il va falloir que tu me montres de quoi tu es capable...qu''est ce que tu vaux face à un adversaire à a ta mesure, est ce que tu sauras faire preuve de l''ingéniosité et de la force nécessaire pour tuer un des tes semblables ? Si tu veux m''en faire la preuve, tu devras affronter six adversaires de ta race qui soient approximativement de la même force que toi... à ce moment là tu pourras revenir me voir.[ID:3]Ok ![/ID:3]\r\n*****\r\n[quete]\r\nTchao ![retour]\r\n*****\r\nHum, peut être qu''après tout, tu n''es pas juste bon a nourrir les manticores...\r\n[ID:5]Ha on va peut être enfin pouvoir parler du sortilège qui sévit sur Myriandre...[/ID:5]\r\n*****\r\nLa fougue de la jeunesse ! mais méfie toi, quoi que ce sortilège camoufle, il a été lancé par quelqu''un avec un pouvoir énorme ! j''ai rarement vu une trame de sort aussi élaborée... mais elle commence déjà a s''effriter, et celui, ou celle, qui l''a lancé, ne prend pas la peine de le renforcer. Peut être n''est il déjà plus sur place... ou peut être pas...\r\n[ID:6]Y a t il un moyen d''annuler le sortilège ?[/ID:6]\r\n*****\r\nVaincre ce sortilège demanderait l''association de certains des mages les plus puissants des royaumes... avec plusieurs semaines devant eux... et sans l''assurance d''y arriver... mais rassure toi, pas besoin de détruire ce sort, il te suffira de le contourner !\r\n[ID:7]et par quel miracle vais je réussir cela ?[/ID:7]\r\n*****\r\nEn théorie c''est simple, il te suffit d''harmoniser ton aura à celle du sort afin qu''il ne te reconnaisse pas comme une cible de l''illusion !\r\n[ID:8]ça a l''air simple comme ça...[/ID:8]\r\n*****\r\nEt bien pour y parvenir il te faudra un enchantement lancé par mes soins qui se chargera de modifier ton aura. Attends un instant.\r\n\r\nAgh barazour, ik-patoum dagorich !\r\n\r\nEt voilà. Vous pouvez maintenant entrer et découvrir les secrets de Myriandre ![quete]' WHERE `pnj`.`id` = 12;
UPDATE `pnj` SET `texte` = '<em>(aspiration/ fumée/ nuage...)</em>\r\n\r\n[non_quete:88]<em>Quelque peu intrigué par votre approche, l''homme rat vous regarde, attendant manifestement une parole de votre part.</em>\r\n\r\n[ID:1]Bonjour, nous sommes nouveaux dans les environs...[/ID:1][/non_quete:88][ISQUETE:88-e1]Zin Ormil : Hum... vous êtes toujours là...\r\n\r\nComme je l''ai déjà dit, il se pourrait que l''un des prisonniers connaisse un moyen de rentrer dans la chambre du chef... Si ma mémoire est bonne, il y a quelques temps, Sam Ergos était allé faire une expédition dans le camp des maraudeurs avec la bonne ZigZag, Ral Zublo et Terence-fu... Sam est un peu une tête brûlée et il est pas très malin mais il pourra sans doute vous en dire plus que moi. Vous le trouverez surement dans une des cellules Kesalys, c''est un habitué de ce genre d''endroit...\r\n\r\nLes prisons sont situées au sud du camp principal des Maraudeurs de Kesalys. Passez le pont que vous avez déjà du traverser pour venir ici et ensuite aller vers l''ouest et le nord. Aller courage...[/ISQUETE:88-e1]\r\n\r\n[quete_finie:88-e1]<em>Zin Ormil vous regarde approcher avec une lueur d''espoir au plus profond des yeux...</em>\r\n\r\n...\r\n\r\nAlors, Où en sommes-nous ? Avez-vous pu entrer dans le repère des maraudeurs ?\r\n\r\n[ID:12]Nous avons fait même mieux Zino ! \r\n\r\nNous nous sommes fait passer pour des maraudeurs et nous avons pu cheminer à notre guise dans les hauts quartiers de Kesalys.\r\n\r\nNous avons plein de chose à te dire mais d''abord première bonne nouvelle : nous avons réussis à avoir un brève discussion avec Azdak de Kesalys qui accepte de vous rencontrer ! [/ID:12]\r\n[/quete_finie:88-e1]\r\n*****\r\n\r\n<em>Zyn vous enjoint à vous présenter...</em>\r\n\r\n[ID:2]Eh bien nous sommes des aventuriers et nous arrivons de la bordure extérieure. Nous avons découvert cette grotte et nous aimerions savoir où nous sommes exactement ?  [/ID:2]\r\n*****\r\n\r\n- La bordure extérieure...\r\n\r\n<em>(gratte gratte) L''homme rat réfléchit un instant puis tire sur son narguilé.</em>\r\n\r\nEh bien, vous êtes ici dans ce que certains d''entre vous jadis, appelaient le "repère des renégats" ? je crois que c''est comme cela qu''ils nous...\r\n\r\n<em>Le scavenger recrache un peu de fumée par les narines avant de reprendre quelques secondes plus tard...</em>\r\n\r\nCet endroit a plusieurs visages, plusieurs blessures, plusieurs vies. Selon le point de vue et l''histoire, il a eut, a et aura sans doute plusieurs noms.\r\n\r\n"Repère des renégats"... oui, ce fut une vision de nous... certains ici la déplore, d''autres y sont attachés. \r\n\r\nCependant, communément, pour nous ici c''est le camp des "Sangs d''exil"\r\n\r\nPour nous cet endroit est celui où ceux qui arrivent à trouver leur place peuvent passer des jours paisibles, jouissant d''une forme de liberté assez rare... Certains n''y arrivent pas, trouvant les conditions de vie trop dures. Les plus faibles ou les moins avantagés ne résistent pas aux affres de cette vie en communauté. Moi je suis connu ici pour être un solitaire érudit. Étant d''un naturel ascétique, aimant lire et fumer, je ne pose pas trop de problème. On me laisse tranquille et je donne quelques "coups de mains intellectuels" quand cela est nécessaire... Car pour d''autres encore, moins... hum, ce lieu est celui de la trahison, de la barbarie ou encore du viol... de territoire...\r\n\r\n[ID:3]Eh bien ! dis donc...\r\nMais qu''entendez-vous par "nous" ? "jadis" ? d''"autres" ? [/ID:3]\r\n*****\r\n\r\n- Mmm mimumimumimm \r\n\r\nTrès bien, ne négligeons pas les présentations. Je m’appelle Zyn Ormil, arrière petit fils du neveu du Roi Zalandar de la contrée d''Azenaer. Si vous connaissez un peu votre histoire, la défunte race d''Azenaer était...\r\n\r\n[ID:4]...composée de scavenger extrêmement agiles ayant développé la capacité naturelle de vivre dans des tempêtes de sable où peu de personnes pourraient ne serait-ce que respirer.[/ID:4]\r\n*****\r\n\r\n<em>Zyn Ormil hoche la tête d''un aire satisfait.</em>\r\n\r\nCette race fut exterminée en grande partie par les Trolls des cavernes de Fazur lors de l''ultime trahison de la femme du Roi Zalandar. Ce dernier, aveuglé par les charmes, ne comprit jamais que sa reine œuvrait depuis longtemps pour son propre intérêt à la victoire du cercle allié des terres de l''ouest...\r\n\r\nBref, quoiqu''il en soit, aujourd''hui, des traces subsistent de ces conflits. A travers les âges, dans les ruines des gênes il y eut des évolutions intéressantes et les "environnements" ont "mutés".\r\n\r\nMême si la majorité des gens ici ne partagent pas mon avis, selon moi, désormais, la défunte race d''Azenaer existe encore sous une autre "forme". Entendez bien que cela n''engage que moi; mais, pour avoir fait des recherches sur les études anthropologique et ethnologiques à propos de cette race, je pense qu''on peut en retrouver des traces caractéristiques dans certaines attitudes... des manières de se mouvoir semblables chez... l''actuelle tribu des Maraudeurs de Kesalys.\r\n\r\n[ID:5]Vous voulez parler du type de chacal munis d''écailles que l''on peut voir si l''on traine plus bas dans les grottes ? [/ID:5]\r\n*****\r\n\r\n- En effet, ce sont une partie d''eux, je pense. \r\n\r\nToutefois cela est difficilement vérifiable. Il faudrait pouvoir faire des test. je n''ai pas le matériel et je ne sais pas comment procéder. Je ne suis pas un scientifique. Je suis plutôt un rat des lettres et de la philosophie...\r\n\r\nMais surtout, nous sommes en très mauvais termes avec les tribu du dessous...\r\n\r\nLe gouverneur actuel ne souhaite pas dialoguer directement avec l''un d''entre nous et il n''accepte pas que nous chassions dans les sous-sols - pour des raisons qui m''échappent, et qui sont de loin prises de manière trop simplistes par la majorité du camp du Sang d''Exil...\r\n\r\nC''est la raison pour laquelle nous logeons ici, près du sol et de l’extérieur. Nous vivons du gibier venant du désert et nous péchons les quelques poissons qui osent encore s''aventurer dans les rares puits d''eau naturelles accessibles à ce niveau.\r\n\r\n[ID:6]Vous dites que personne ici ne semblent se soucier des raisons qui incitent les Maraudeurs de Kaselys à ne pas accepter de partage de territoire. Ces Maraudeurs ne sont-ils tout simplement pas racistes ? N''ont-ils pas peur de vous et de votre intelligence ? [/ID:6]\r\n*****\r\n\r\nLes maraudeurs de "kEsAlys", on dit, et non "kAsElys" ne sont ni racistes, ni dénués d''intelligence. Leur nom précis a une importance considérable puisqu''ils se sont affublés eux mêmes de ce nom à partir du moment ou nous avons essayé - en vain - de négocier une entente cordiale avec eux. \r\n\r\nAvant cela ils n''avaient pas de nom. Ils s''interpelaient de diverses manières selon les grées du temps et des marées. J''ai fait des recherches sur l''origine de "Kesalys". Je n''ai trouvé nulle trace de ce nom dans aucun des livres que j''ai pu collecter durant toute ma longue vie. \r\n\r\nCependant, je connais une version de l''origine de ce nom. D''après un maraudeur avec qui j''ai eu la chance de sympathiser il y a quelques années, ce nom proviendrait de fresques présentes dans les étages inférieurs de la grotte. D''après lui, des figures étranges de peuples plus anciens auraient donné par juxtaposition de leurs courbes et de leurs formes géométriques : "Ke sal ys". \r\n\r\n[ID:7]Des fresques murales dans les profondeurs des grottes ? !! \r\nC''est interessant...[/ID:7]\r\n*****\r\n\r\nOui... une forme d''art pariétale à la logique assez complexe. Et l’interprétation de ces dessins va plus loin selon ce maraudeur. Ces figures auraient été celles de peuples qui vénéraient l''eau et l''ombre des rochers. Pour eux, l''ombre des choses manifestait leur vie et l''eau symbolisait la mort nécessaire venant perturber, animer ou faire disparaitre chaque ombre.\r\n\r\nToujours d''après ce maraudeur, la figure sacrée et vénérée au plus haut point serait naturellement celle du nuage. Puisque évanescentes, changeantes, donnant la pluie et n''ayant que des ombres quasi invisibles ou difficilement prévisibles, les limites des nuages seraient les mécanismes les plus puissants. De ces formes proviendrait la vie et les notions de répétition, de silence et de distance qui leur sont chères. \r\n\r\nAinsi, non, Les Maraudeurs de Kesalys ne sont pas racistes, bêtes ou je ne sais quoi encore. Ils ont une forme d''intelligence et de croyance qui - même si elle diffère un peu de la notre - est très développée.\r\n\r\nDans ce camp la plupart des personnes ne se soucient que d''eux mêmes et n''ont probablement jamais réfléchis à la présence de culture chez les Maraudeurs. Ils les prennent ni plus ni moins pour des animaux capables de créer certaines formes fixes. Si on suit leur cheminement, en théorie cela reviendrait, à peu de chose près, à dire que dès la naissance des maraudeurs les gènes dépasseraient leur corps pour vivre dans l''espace qu''ils s''octroient...\r\n\r\nMais je rejette ce point de vue car j''ai pu vivre avec certains d''entre eux il y a quelques années et je vois bien qu''ils ne pensent pas de manière déterminé et qu''ils sont étonnants de créativité et d''étrangeté.\r\n\r\n[ID:8]Mais pourquoi avoir perdu le contact avec ces maraudeurs.. vous dites que vous en avez côtoyé certains et vous parlez de fresques, de culture de tout un monde qui nous est étranger et que personne ici ne semble vouloir explorer... Une mine d''or et de légende s''étale sous vos pieds et vous ne tentez rien pour en découvrir les propriétés...[/ID:8]\r\n*****\r\n\r\nLa situation n''est pas aussi simple que cela. Il y a des considérations actuelles d''ordre socio-politiques, économiques et géologiques qui nous en empêchent. J''ai tenté en vain de parler au chef des maraudeurs de Kesalys mais depuis qu''ils se nomment ainsi, depuis 3 ans environ, je n''ai eu que des réponses hostiles quand ce n''était pas de l''indifférence. Depuis je me contente de tempérer les hardeurs des Sangs d''Exil quand il y a des escarmouches. Je discute les lignes de territoire et cela s''arrête là.\r\n\r\nJe n''ai jamais été doué pour l''art de la guerre et de la violence. J''ai toujours préféré la dissuasion mais désormais, nous sommes contraint à occuper uniquement le niveau 0 de cette caverne et nous finissons par nous en accommoder plus ou moins avec le temps.\r\n\r\n[ID:9](...)\r\n\r\n(Hésitations)\r\n\r\nBon,\r\n\r\nÉcoutez, nous sommes aventuriers et nous pourrions peut être vous aider.\r\n\r\nEn fait... nous ne sommes pas là entièrement par hasard. Au fil de nos quêtes nous avons amassé des trésors et des légendes. Lors d''un voyage dans des galeries souterraines gobelines nous avons pu lire quelques passages d''un petit ouvrage. Il nous était compté l''histoire de la guerre entre les gobelins, les trolls. De fouilles archéologiques menées par le roi gobelin avec l''aide d''une équipe de spécialistes de diverses races. C''était une sorte de compte rendu. Il était aussi dit...[/ID:9]\r\n*****\r\n\r\nTiens donc... Si vous parlez de l''analyse des strates de Saltus je connais cet ouvrage. Je ne l''ai jamais lu mais j''en ai entendu parler. Je sais qu''il est l''un des seuls à décrire les guerres et les événements de cette période mouvementée. Mon grand père a très bien connu Pery la douce, la mère d''Aylen Destryse qui est l''auteur de ce livre. Pery La douce était, parait-il, une femme elfe passionnante. Quel dommage qu''elle ne soit plus de ce monde... Sa fille est quant à elle... Enfin bref, cet ouvrage dont vous avez eu connaissance est très singulier. Il compte des histoires qui ont leurs importances...\r\n\r\n[ID:10]Oui. Ces légendes se diffusent par ceux qui ont vaincu le roi gobelin en passant outre l''abomination qui sévit dans les profondeurs. Nous avons déjà une certaines expérience des galeries souterraines et dans ce petit livre était évoquée l''hypothèse d''une cité enfouie sous la 30eme strate du lac de Saltus... [/ID:10]\r\n*****\r\n\r\nEn effet, il est très probable qu''une cité ancienne s''étendant sur des milliers de kilomètres soit présente en dessous de nous dans ces grottes... Pas seulement à côté du lac Saltus donc, même si c''était un endroit interessant pour y faire cette découverte.\r\n\r\nHum eh bien, si vous comptez vraiment nous aider et explorer plus loin dans les profondeurs, je dois vous prévenir cependant que la tache ne sera pas aisée...\r\n\r\nLes maraudeurs sont nettement moins présents dans les 2eme et 3eme sous-sols. Les accès à ces strates inférieures ne sont pas directement bloqués mais il y a une raison à cela : des créatures d''une puissance qui dépasse largement les arts de la chasse des maraudeurs pullulent ici bas.\r\n\r\nCependant, il existe des passages scellés par quelques mécanismes ou sortilèges pour lesquels je n''ai pas de solution et il vous faudra sans doute négocier avec les maraudeurs... De plus, je dois bien avouer que les temps ont changés et que les marées ne sont plus aussi clémentes qu''autrefois...\r\n\r\nAh mon humble avis, vous n''arriverez à rien de satisfaisant plus bas si vous ne tissez pas des liens avec certains maraudeurs. Eux connaissent bien mieux que moi les profondeurs des marées et leur mécanismes. Il faudrait que vous arriviez à vous infiltrer dans les quartiers importants des campements Kesalys et si vous arrivez à parler à celui qui les dirige, vous pourriez tenter d''en savoir davantage sur les raisons qui les poussent à ne pas coopérer avec nous.\r\n\r\n[ID:11]Nous allons tenter d''en savoir plus. Nous faisons de votre diplomatie notre affaire la plus urgente ! Vous pouvez comptez sur nous noble Zyn Ormil. Merci pour toutes ces précieuses informations.[/ID:11]\r\n*****\r\n\r\nJe vous en pries. Revenez me voir dès que vous aurez du nouveau.\r\n\r\nAh attendez ! Une dernière chose : il existe une prison à l''étage inférieure dans laquelle des créatures et des sangs d''exil sont enfermés. C''était un de nos quartiers avant que les maraudeurs ne nous en chassent. Certaines salles sont utilisées comme des geôles par les maraudeurs... Vous devriez parler à certains renégats prisonniers. Ils pourront peut être vous aider.\r\n\r\nBonne chance.\r\n\r\n<em>Vous acceptez de venir en aide au camp du sang d''exil en faisant la quête "Améliorations des relations diplomatiques".</em>[prendquete:88]\r\n*****\r\n\r\nExtraordinaire, jamais je n''aurais pensé que...\r\n\r\nMais dites m''en plus, allez donc ! \r\n\r\n[ID:13]Son valet était endormi et nous sommes entrés lorsque Azdak était en train de prendre son bain. Il nous a prit pour des espions et nous avons pu discuter avec lui. On a bien essayé de le raisonner mais sans trop de succès. Il semblerait que ses conseillers aient une grande influence sur lui. Toutefois nous avons réussis à obtenir un rendez-vous avec vous, ce qui prouve qu''il n''est pas complétement manipulé.[/ID:13]\r\n*****\r\n\r\nSes conseillers... <em>Zyn Ormil tire sur le narguilé avant de lâcher</em> : j''ai entendu parlé deux.\r\n\r\nD''après les rumeurs, ils seraient originaire de l''ancienne ville des profondeurs mais comme jamais aucun d''entre nous n''a pu y entrer, cela reste peu probable ou alors invérifiable. \r\n\r\n[ID:14]Nous savons Zino. Et c''est pour cela que travailler avec Léonard Sileyn serait avantageux.\r\n\r\nPour entrer dans la cité du dessous, des canalisations doivent être réparées mais le Maraudeur Brizen Dresco les vend trop chère - même avec l''amélioration des relations... Léonard semble bien connaitre ce vendeur et si on travaille avec lui il nous fera bénéficier d''un bon de réduction en échange d''un pourcentage sur ce que nous trouverons dans la ville des profondeurs. Cependant, il est clair que Léonard ne semble pas très fiable... [/ID:14]\r\n*****\r\n\r\nC''est le moins que l''on puisse dire. Beaucoup de mauvaises rumeurs circulent à propos de cette homme. Néanmoins, il connait bien mieux que moi les circuits et c''est lui qui détient le commandement des forces armées de ce camp... Donc de toute manière, si vous n''aviez pas accepté de travailler avec lui, vous n''aurez rien pour faire...\r\n\r\n[ID:15]Oui...Malheureusement.\r\n\r\nSinon, Zino, au centre des hauts quartiers Kesalys, il y a une drôle de fontaine avec un arbre entouré de poisson qui circulent en rond; cela vous inspire-t-il quelque chose ?[/ID:15]\r\n*****\r\nOui, c''est l''arbre des sèves Kesalys. J''ai oublié de vous en parler mais c''est en effet un élément très important sur quoi s''est basée la culture des Maraudeurs de Kesalys.\r\n\r\nAu commencement, il y a quelques années, lorsque j''avais encore accès à cet endroit, la fontaine n''existait pas encore. Il y avait juste cet arbre étrange sortant du sable, à l''écorce de pierre, à moitié fossilisé, sans feuilles et suintant d''une sève aux propriétés inconnues. \r\n\r\nTrès vite, pour des raisons qui m''échappent encore, les Maraudeurs de Kesalys se sont mis à vénérer cet arbre et ils ont alors décidé de construire un monument autour de lui. Une sorte de muret a ainsi été érigé tout autour.\r\n\r\nQuelques temps passèrent et lors d''un changement des marées, de la pluie est tombée de cette arbre et les murs qui avaient été construits se sont alors transformés en parois naturelles de la fontaine que vous connaissez désormais.\r\n\r\nEn revanche, je n''étais pas au courant de l’existence de petits poissons reliés par des fils, eux mêmes attachés à une branche en mouvement... C''est passionnant, vite, il faut vraiment que j''aille voir ça de plus près.\r\n\r\n<em>Zino fait mine de commencer à ranger ses affaires pour partir et tout en changeant d''habit, il continue à parler :</em>\r\n\r\nBon, faites attention dans les grottes et ne faites pas confiance à Léonard.\r\n\r\nJe vais vraiment devoir y aller maintenant. On ne laisse pas attendre le gouverneur des Kesalys surtout dans le contexte actuel...\r\n\r\n<em>Zino ne vous regarde même plus, il murmure pour lui même : Ou est donc passé ma baguette de Tantium ah et un petit cadeau serait le bienvenu tiens la voila aller hop mon plus beau chapeau. Que j''ai hâte de voir Azdak, l''arbre, les poissons et toutes les autres choses...</em>\r\n\r\nMes amis, si vous n''avez rien de très important à me dire...\r\nA bientôt et merci encore pour votre aide.\r\n[quete]\r\n\r\n' WHERE `pnj`.`id` = 22;
UPDATE `pnj` SET `texte` = '[non_quete:88-e5]ouh ouh ouh huh hu\r\n\r\n(gémissements)\r\n\r\n(larmes) \r\n\r\n(alcool) \r\n\r\n(dépression)[/non_quete:88-e5][isquete:88-e5]S''ils pouvaient me rendre mon césar ouh ouh ouh huh hu\r\n\r\n(gémissements)\r\n\r\n(larmes) \r\n\r\n(alcool) \r\n\r\n(dépression)\r\n\r\n[ID:1]Aller ça va s’arranger...\r\n\r\n(..................)[/ID:1][/isquete:88-e5]\r\n[ISQUETE:88-e6-v1]Alors du nouveau ? \r\n\r\nNon pas de nouveau...\r\n \r\n(gémissements)\r\n\r\n(larmes) \r\n\r\n(alcool) \r\n\r\n(dépression)\r\n\r\n[/ISQUETE:88-e6-v1][non_quete:88-e6-v2]\r\n(gémissements)\r\n\r\n(larmes) \r\n\r\n(alcool) \r\n\r\n(dépression)[/non_quete:88-e6-v2][quete_finie:88]Jamais je ne pourrais vous remercier à la mesure de ce que vous avez fait pour moi et le camp des Sang D''exil.[/quete_finie:88]\r\n*****\r\n\r\n<em> Arbus Tendre-Flocon, le grand barbare avachie sur sa chaise en train de boire une pinthe vous apostrophe :</em>\r\n\r\nHey là étrangers, ne faites pas attention à Gazius. Comme beaucoup de renégats ici, ce bougre a perdu récement un rejeton... (ips) C''était un petit garçon qui s''appelait César. Il était assez vif pour son âge et aimait bien jouer avec mon fils Riky, cependant il était assez maladroit (ips) et frappé d''une sorte de maladie que nous n''arrivions pas à guérir... (ips)\r\n\r\nIl est allé jouer avec Riky (ips) et Lyly (ips) et a disparu sans laisser de trace... Depuis j''interdis mon fils (ips) d''aller se cacher dans les strates inférieures car avec ses maudits chacals qui rodent... <em>le barbare semble soudainement s’énerver, puis reprenant ses esprits, il continue :</em>\r\n\r\nBouarf, laissez (ips) tombé ces histoires sont bien trop tristes... passons à autre chose de plus (ips) joyeux...\r\n\r\n<em> Le nain qui écoutait avec attention le barbare sans cesser de gémir dans un râle lancinant, fit jaillir sa bière et se redressant sur sa chaise hurla d''une traite :</em> \r\n\r\nESPÈCE D’IVROGNE D''ARBUS, TU PEUX LE DIRE !!!! TU PEUX LEUR DIRE !!! C''EST NOTRE FAUTE A TOUS SI NOS ENFANTS SON DÉGÉNÉRÉS ET QU''ILS DISPARAISSENT !! REGARDE NOUS !! IL N''Y A PRESQUE PLUS D''ENFANT ICI...\r\n\r\nNous allons bientôt tous mourir sans aucune descendance, par notre égoïsme, notre petit confort...\r\n\r\n[ID:2]Si vous voulez, nous pouvons vous aider à tirer cette affaire au clair...[/ID:2]\r\n*****\r\n\r\n(gémissements)\r\n\r\n(larmes) \r\n\r\n(alcool) \r\n\r\n(dépression)\r\n\r\n(huh ?)\r\n\r\nVous feriez ça ? !! (larmes de joie)\r\n\r\n[ID:3](..................) eh bien oui...[/ID:3]\r\n*****\r\n\r\nLe nain se torche le nez avec sa choppe de bière et vous tape dans la main.\r\n\r\n<em>Vous acceptez de rendre un service qui n''a pas de prix...</em>\r\n[quete:1]\r\n\r\n\r\n\r\n\r\n\r\n' WHERE `pnj`.`id` = 23;
UPDATE `pnj` SET `texte` = '[non_quete:88]<em>A votre approche l’homme se relève et s''adosse à une armoire. Il vous scrute un moment d''un regard teinté de sentiments que vous n''identifiez pas clairement. Son visage masqué, à la fois sombre et bienveillant vous place dans un rapport de force psychologique complexe qui ne vous donne pas vraiment envie d''être sympathique.</em>[non_quete:88][isquete:88-e3]<em>A votre approche l’homme se relève et s''adosse à une armoire. Il vous scrute un moment d''un regard teinté de sentiments que vous n''identifiez pas clairement. Son visage masqué, à la fois sombre et bienveillant vous place dans un rapport de force psychologique complexe qui ne vous donne pas vraiment envie d''être sympathique.</em>\r\n\r\nMes salutations étrangers, je m''appelle Leonard Sileyn.\r\n\r\n[ID:1] (...) Que voulez vous au juste ?[/ID:1][/isquete:88-e3]\r\n[ISQUETE:88-e4]Alors du nouveau ? \r\nNe me dites pas que vous abandonnez déjà ? \r\n\r\n<em>L''homme se met à rire aux éclats pendant quelques secondes... avant de retourner à ses occupations... [/ISQUETE:88-e4]\r\n[quete_finie:88-e4]Ah vous revoilà, vous avez fait du bon travail je suis fier de vous...\r\n\r\nNotre collaboration s''arrête ici. Au revoir.[/quete_finie:88-e4]\r\n*****\r\n\r\nEh beh hehh. \r\nPour dire vrai, dès votre arrivée, il me semble avoir décelé, dans votre allure, l''étoffe de fiers aventuriers aux talents supérieurs à la plupart des abrutis arrogants qui trainent dans ce camp.\r\n\r\n[ID:2]Ah ? eh bien...[/ID:2]\r\n*****\r\n\r\nNe faites pas les modestes... je n''ai pas pour habitude de faire des compliments, profitez-en...\r\n\r\nJe suis d''un naturel assez discret... Je mène mes petites affaires de mon côté et jusqu''ici personne n''a eut à se plaindre de mes services. Donc pour aller droit au but, je sais que vous voulez explorer les sous-sols en dessous de la strate 30 et - pour tout vous avouer - cela fait un moment que je ressens aussi ce désir.\r\n\r\n[ID:3] Euh... oui en effet...mais... [/ID:3]\r\n*****\r\n\r\nBon eh bien, justement, pour descendre, vous aurez sans doute besoin de mon aide. \r\n\r\nJe m''explique. \r\n\r\nNon loin d''ici, au premier sous-sol vit un vendeur chacal. Vous pouvez difficilement passer à côté mais bref, si vous ne l''avez pas encore rencontré, vous pouvez être sure qu''il sera très peu enclin à vous vendre quoi que ce soit à un prix raisonnable. Ce bougre est spécialisé dans la contrebande de pierre précieuse et vu que le filon est épuisé depuis un bon paquet de temps (enfin c''est ce qui se dit...) il ne vend plus que des morceaux de roches étranges ramenés par des troupes d''élites chacals de leurs excursions dans les profondeurs. Son stock est inépuisable mais il ne fait des prix qu''aux membres de sa race... sauf exception...\r\n\r\n[ID:4]Seriez-vous en train de prétendre que...[/ID:4]\r\n*****\r\n\r\nNon...\r\n\r\n......\r\n\r\nenfin...\r\n\r\nBon disons qu''en échange de certains services ce vendeur me cède ce dont j''ai besoin à des prix de chacal héh...\r\n\r\nEt si vous acceptez de bosser pour moi, je peux vous faire profiter de réductions avantageuses. \r\n\r\n[ID:5] Franchement, pourquoi aurions nous besoin de vous et de ces pierres hors de prix fournies par ce vendeur chacal raciste ?!!! Nous allons nous rendre dans les profondeurs par nos propres moyens avec ou sans votre aide.\r\n\r\n<em>Vous faites mine de sortir de la tente...</em>[/ID:5]\r\n*****\r\n\r\nEh là !!!\r\n\r\nPas si vite...\r\n\r\nCes roches servent en réalité à ouvrir certains passages scellés magiquement. Il semblerait qu''elles appartiennent à un réseau de canalisation complexe érigé il y a de cela fort longtemps qui traverse en hauteur toutes les strates inférieures des cavernes présentes en dessous. Elles sont donc essentielles pour votre progression et le vendeur le sait bien... c''est pour cette raison - entre autres - qu''il les vend aussi cher.\r\n\r\n[ID:6]Un réseau de canalisation ? Dans des zones à fortes marées ? C''est une blague ? ![/ID:6]\r\n*****\r\n\r\nNon.\r\n\r\nVous verrez vous même que tout cela est très sérieux. Les marées n''ont pas toujours été si violentes... Et cette roche n''est pas commune. Elle puise dans l''eau qui coule sur elle des propriétés qu''elle conduit et communique tout au long du réseau auquel elle participe... \r\n\r\nVers on ne sait où...\r\n\r\nD''ailleurs c''est en partie pour découvrir jusqu''où vont ces canalisations que j''aimerais vous aider mais il faudrait me tenir informer en premier de vos avancées. Et si jamais dans votre chance vous découvrez quelques objets précieux... eh bien naturellement je prendrai 50 % de leurs valeurs.\r\n\r\n[ID:7]Comment !! ?[/ID:7]\r\n*****\r\n\r\nC''est à prendre ou à laisser mais à bien regarder... Vous n''avez pas l''air si fort que cela en fait... et pas des plus riches non plus... Et en définitive j''ai bien peur que je ne me sois adressé à des gamins plus téméraires et arrogants que certains des meilleurs guerriers du sang d''exil... alors ?\r\n\r\n[ID:8](Grognement/ réflexions) Nous sommes des aventuriers vertueux et nous allons vous le prouver ![/ID:8]\r\n*****\r\n\r\n[ID:9]Cependant nous prenons trop de risques, donc cela sera 25 % pour vous, 75 % pour nous et rien d''autres.[/ID:9]\r\n*****\r\n\r\nAh non, disons plutôt 45 % pour moi et 55 % pour vous.\r\n\r\n[ID:10]Non, 30 % pour vous, 70 % pour nous![/ID:10]\r\n*****\r\n\r\n40 % / 60 % !!\r\n\r\n[ID:11]35 % / 65 % ![/ID:11]\r\n*****\r\n\r\nTrès bien, très bien... disons 37 % pour moi et 63 % pour vous !\r\n\r\nC''est d''accord ?\r\n\r\nAller. Tenez ! Prenez ce bon de réduction et donnez le de ma part à ce bon vieux Brizen Dresco.\r\n\r\n[ID:12] (...) Eh bien d''accord... Au fait juste une question... [/ID:12]\r\n*****\r\n\r\nMouais ?\r\n\r\n[ID:13]Avez vous voyagé dans la bordure extérieure ? [/ID:13]\r\n*****\r\n\r\nLa bordure extérieure ? \r\n\r\n<em>L''homme ne semble visiblement pas comprendre de quoi vous voulez parler, puis réfléchissant quelques secondes : </em> Ah vous voulez parler du continent où vous vivez ? (rire) Oui, je suis originaire du haut quartier de Narcisse... Bon mais je n''ai pas le temps de parler de ma vie privée. Excusez moi, il se fait tard et je dois aller régler quelques affaires urgentes... je vous laisse. Tenez moi au courant de vos trouvailles.\r\n\r\nCertes... Léonard... A bientôt et bonne continuation dans vos petites affaires...\r\n\r\n[quete]Vous acceptez de profiter des services de Léonard en faisant la quête "Exploration des strates inférieures"\r\n*****\r\nEt bien allez-y !' WHERE `pnj`.`id` = 28;
UPDATE `pnj` SET `texte` = '[quete_finie:88-e1]\r\n(splash...splash, splash, double splash. soupir d''apaisement...)\r\n\r\n<em>Le chef des maraudeurs se prélasse devant vous dans sa baignoire...\r\nQuelques secondes passent en silence avant que ce dernier ne remarque votre présence.</em>\r\n\r\n(gratte vent) + (regard cercle) = (Vous pouvez disposer. Merci.)\r\n[/quete_finie:88-e1]\r\n[non_quete:88](splash...splash, splash, double splash. soupir d''apaisement...)\r\n\r\n<em>Le chef des maraudeurs se prélasse devant vous dans sa baignoire...\r\nQuelques secondes passent en silence avant que ce dernier ne remarque votre présence.</em>\r\n\r\n(gratte, gratte) + (triple coup de queue sur la tête) + (2 sec de sifflement) + (double coup de queue sur le torse) + (regard noir) = (Qui ose venir me déranger pendant l''heure du bain ?!! Sortez tout de suite !!)[/non_quete:88]\r\n[isquete:88-e1]\r\n(splash...splash, splash, double splash. soupir d''apaisement...)\r\n\r\n<em>Le chef des maraudeurs se prélasse devant vous dans sa baignoire...\r\nQuelques secondes passent en silence avant que ce dernier ne remarque votre présence.</em>\r\n\r\n(gratte, gratte) + (triple coup de queue sur la tête) + (2 sec de sifflement) + (double coup de queue sur le torse) + (regard noir) = (Qui ose venir me déranger pendant l''heure du bain ?!!)\r\n\r\n[ID:1](gratte, gratte) + (secoue oreille gauche) + (quadruple lèche épaule droite) + (triple douce caresse sur eau) = (Désolé de vous importuner ainsi pendant votre bain, noble seigneur des strates, mais nous désirons vous parler de choses importantes...[/ID:1][/isquete:88-e1]\r\n*****\r\n\r\n(gratte, gratte) + (caresse dans l''eau) + (ondulation de queue vers le haut) + (double coup de patte dans l''eau + multiples rotations des oreilles vers l''avant) = (Qui êtes vous d''abord !? Eh pourquoi ne m''a t-on pas prévenu de votre présence ? !)\r\n\r\n[ID:2](gratte toison écaillé sans motivation) + (courbette lente et molle alternée de coup de patte hésitant dans le vide) + (combinaison de coup de queue complétement hasardeux et sifflement dissonant + bâillement) = (...euh... eh bien nous sommes des... nous sommes en mission spéciale.. votre chambre était en train de dormir... enfin je veux dire votre garde...)[/ID:2] \r\n\r\n<em>Azdak sent que vous n''êtes pas bien dans votre peau...</em>\r\n*****\r\n\r\n(gratte oreille, regard vers le haut) + (râle + soupir) + (coupe de queue dans l''eau) + (tape patte et rebondissement soudain) = (En mission spéciale ? ... et mes gardes dorment... non mais vraiment... Bon eh bien puisque vous êtes là, qui y a-t-il de si urgent au point de me déranger ainsi ?)\r\n\r\n[ID:3](gratte museau hésitant) + (squames rougissant) + (caresse dans l''eau) = (Eh bien, si vous le permettez, nous aimerions vous parler du camp des Sangs d''Exil. Oui, emmm.. Ils ont l''air plutôt sympas non ?)[/ID:3]\r\n*****\r\n\r\n(pince squames sous l’œil) + (coup de queue violent dans l''eau) + (retournements rapide alternés avec coup de patte droit dans la pierre) (gratte, gratte, gratte écailles cuisses gauche) = (C''est pour me parler de ça que vous me dérangez !! ? Ne vous ai-je pas déjà expliqué qu''ils étaient mauvais ? Mes conseillers m''ont dit des choses affreuses sur eux !!! Mieux vaut ne pas s''en approcher.)\r\n\r\n[ID:4] (sourcil écaillé levé) + (coup de patte sur toison écaillé) = (Mais de quelle choses affreuses vous ont-ils parlé ?)[/ID:4]\r\n*****\r\n\r\n(coup de queue dans l''eau) + (miaulement strident) + (queue droite) + (squames violet) + (tape patte dans l''eau) = (Il semblerait que les renégats soient des bêtes sanguinaires, sans âmes et sans cervelles et qu''ils seraient prêt à tout pour détruire notre culture. Par ailleurs j''ai entendu dire qu''ils avaient pour coutume de se débarrasser des plus fragiles de leurs descendants ; pratiques que le peuple des Kesalys ne peut accepter!)\r\n\r\n[ID:5](sourcil redondant écaillé levé) + (dix coups de griffe dans l''eau) + (long sifflement alterné de scintillement d''écailles) + (lèche moustache) = (Sans vouloir vous offenser, certains renégats ne sont pas très subtils mais dans l''ensemble ils sont pacifiques. Les pratiques barbares dont vous parlez ne sont pas celles des Sangs d''Exil. Sans vouloir vous vexer votre excellence, vous semblez être mal informé. Justement, certains membres du camp des Sangs d''Exil pensent que leur enfants se sont perdu ou ont été kidnappé par des maraudeurs...)[/ID:5]\r\n*****\r\n\r\n(tape dans l''eau) + (écailles brillantes de multiples couleurs) + (coup de queue dans la pierre) = (Toutes ces affaires m''ennuient... et d''abord comment savez-vous cela ? Vous avez sympathisé avec eux ?) \r\n\r\n(silence d''une minute) + (queue ondulante comme un serpent) + (écailles bleu azur) + (crête verte tirée vers l''arrière) + (clin d''oeil arc en ciel) = (Ah ! ça y est j''y suis, vous êtes ces gredins qui ont été envoyé il y a quelques mois en mission d''espionnage dans le camp des renégats... Voila...tout s''explique, vous venez me faire votre rapport... vous auriez pu le dire plus tôt au lieu de vous confondre en excuse dans des attitudes si bizarres... Bon mais admettons que vous ayez raison, quel intérêt auraient mes conseillers à me dire de tels mensonges ?)\r\n\r\n[ID:6](clignement d''oeil) + (hochement de crête) + (coup de patte sur toison écaillée) + (courbe queue) = (Peut être que vos conseillers n''en savent rien ? et d''ailleurs d’où les connaissez-vous ? Vous ont-ils donné des preuves de la barbarie des Sang d''Exil ?)[/ID:6]\r\n*****\r\n\r\n(clignotement intense des micro écailles frontales) + (caresse horizontale de la pierre) + (mouvement lent de queue) = (Non, aucunes preuves... mais j''ai confiance en eux. Ils ont montré à plusieurs reprises qu''ils connaissaient très bien certains endroits des strates inférieures que le peuple des Kesalys ne connaissait pas et ils ne m''ont jamais menti. Ils nous ont appris notre histoire et cela fait d''eux des personnes exceptionnelles!)\r\n\r\n[ID:7](silence précieux) + (agitation des lèvres) + (queue agitée) + (coups de patte dans le vide) + (pirouette sur vous même) + (alternance de courbettes) = (Écoutez, votre excellence, nous avons passez 4 mois à espionner la vie des renégats et nous avons été étonné de voir qu''ils n''avaient rien de semblables à des brutes sanguinaires... Les Sangs d''Exil ont leur tempérament et ont plein de defauts. Ils sont égoïstes, mais au sein de leur camp, règne un certains calme. Nous avons rencontrer un homme rat qui nous ressemble un peu. Il s’appelle Zin Ormil. Il nous a dit qu''il avait essayé de vous rencontrer... sans succès. Il aimerait que les choses s’améliorent; Il a l''air très érudit...)[/ID:7]\r\n*****\r\n\r\n(soupir) + (écailles vert luisantes) + (crachat dans l''eau) = (Zin Ormil vous dites... Ce nom ne m''est pas étranger mais je n''arrive plus à savoir... Eh bien soit.. En y repensant, il est vrai que la manière dont mes conseillers m''ont parlé des Sangs d''Exil était assez étrange... Bon, je veux bien rencontrer ce Zin Ormil. Dites lui qu''il peut venir quand il le désire pour faire connaissance et qu''il ne sera pas mal traité.)\r\n\r\n[ID:8](sifflement discret) + (gratte écaille) = (très bien votre excellence, vous faites bien, nous y allons de ce pas)[/ID:8]\r\n*****\r\n[quete]' WHERE `pnj`.`id` = 29;
UPDATE `pnj` SET `texte` = '<em>(gratte gratte)\r\n\r\n(gratte oreille droite)\r\n\r\n(lèche patte gauche)</em>\r\n\r\n<em>(Ho ! des clients !)</em>\r\n\r\n(Bonjour !)\r\n\r\n(écailles luisantes) + (arabesques poilues) + (sauts saccadés alternés de coup de queue combinés à des figures et des coups de griffes) + (gratte gratte) + (zigzag courbe queue) + (sifflement) = (Je vends tout un tas de minerais. Du charbon, du quartz, au titane, jusqu''au fameux tantium...issus d''anciennes canalisations. Dites moi ce que vous voulez. Si vous voulez du tantium je préviens c''est très cher. ça vous fera 10000 stars. On dit que ces morceaux de roches incurvés sont magiques et qu''ils sont indispensables pour se rendre dans certaines parties des grottes...)\r\n\r\n[ID:1](courbe pied + gratte menton + doigt ciblant le morceau de roche) = (Je vais vous prendre de la roche tantium incurvé.)[/ID:1]\r\n[quete_finie:88-e1][ID:2](coup de main et caresse dans le vent) + (gratte gratte) + (gratte gratte en zigzag) + (étirement bas haut alterné) + (courbette) = (Je sais pas si vous suivez les rumeurs, mais nous faisons partie des personnes qui ont contribué à améliorer les relations diplomatiques entre le camp des renégats et celui des Kesalys... Nous avons vu Azdak de Kezalys...)[/ID:2][/quete_finie:88-e1]\r\n[quete_finie:88-e3][ID:3](tape sur le derrière) + (bon de réduction) + (regard acide) (vous vous dites que ça devrait le faire niveau autorité :)) = (Hey je bosse pour Leonard Sileyn! Il m''a dit de venir vous voir avec ce bon de réduction...)[/ID:3][/quete_finie:88-e3]\r\n[ISQUETE:88-e3][ID:3](tape sur le derrière) + (bon de réduction) + (regard acide) (vous vous dites que ca devrait le faire niveau autorité :)) = (Hey je bosse pour Leonard Sileyn! Il m''a dit de venir vous voir avec ce bon de réduction...)[/ID:3][/ISQUETE:88-e3]\r\n*****\r\n<em>(rire narquois)</em>\r\n\r\n(regard triangle)\r\n\r\nMerci bien !\r\n\r\n[vendsitem:o57:10000]\r\n*****\r\n(gratte museau) + (regard vers le haut + toison virant vers le bleu turquoise) + (gratte gratte) + (queue courbée) = (Oui je sais. Espérons que cela soit une bonne chose pour les affaires...Bon, pour vous remercier, je vous fais moitié prix, ok ?)\r\n\r\n[ID:4](main levé vers l''avant) + (tape cuisse) = (Ah ça c''est un geste commerçant ! Tope là !)[/ID:4]\r\n[quete_finie:88-e3][ID:5](courbe fesse) + (regard éclair) + (vous montrez le bon de réduction) = (hum, Leonard Sileyn m''a donné ce bon de réduction pour acheter des morceaux de canalisation...)[/ID:5][/quete_finie:88-e3]\r\n[ISQUETE:88-e3][ID:5](courbe fesse) + (regard éclair) + (vous montrez le bon de réduction) = (hum, Leonard Sileyn m''a donné ce bon de réduction pour acheter des morceaux de canalisation...)[/ID:5][/ISQUETE:88-e3]\r\n*****\r\nTrès bien, très bien. Je vois... Bon je consent donc à vous faire moitié prix ; Ça fera 5000 stars.\r\n\r\n[ID:4](tape cuisse)= (Tope là !)[/ID:4]\r\n[quete_finie:88-e1][ID:5](étirement bas haut alterné) + (courbette) = (Mais j''ai aussi amélioré les relations diplomatiques !)[/ID:5][/quete_finie:88-e1]\r\n*****\r\n(regard triangle)\r\nMerci bien !\r\n[vendsitem:o57:5000]\r\n*****\r\n(gratte gratte ) + (queue courbée)\r\nHa ?? Ça aussi ?\r\nHum ...\r\n\r\n(queue arquée) + (regard violet) + (toison noir) + (gratte gratte équerre) + (paupière fermée verte) = (C''est du vol, j''y perds des plumes, mais je peux vous faire 25% de réduction supplémentaire. Ça vous fera -- pour vous seulement -- 2500 stars !)\r\n\r\n[ID:6](regard enjoué) + (tape cuisse) = (voici une bonne proposition !)[/ID:6]\r\n*****\r\n(courbe queue) + (museau suintant) + (squames virant au jaune) = (heureusement que tous les clients ne négocient pas aussi bien que vous, j''y perdrais ma chemise !)\r\n[vendsitem:o57:2500]' WHERE `pnj`.`id` = 33;
UPDATE `pnj` SET `texte` = '[non_quete:88-e5](gratte gratte) + (coup de tête dans le vide) + (crachat) = (Tiens donc voila encore un de ces braves fantoches du Général Bartak)[/non_quete:88-e5][isquete:88-e5](gratte gratte) + (coup de tête dans le vide) + (crachat) = (Tiens donc voila encore un de ces braves fantoches du Général Bartak)\r\n\r\n[ID:1](...)[/ID:1][/isquete:88-e5]\r\n[ISQUETE:88-e5-v1](gratte gratte) + (coup de tête dans le vide) + (crachat) = (Tiens donc voila encore un de ces braves fantoches du Général Bartak)[/ISQUETE:88-e5-v1]\r\n[ISQUETE:88-e5-v2](gratte gratte) + (coup de tête dans le vide) + (crachat) = (Tiens donc voila encore un de ces braves fantoches du Général Bartak)\r\nNe revenez pas avant d''avoir vraiment des nouvelles informations, on nous surveille...[/ISQUETE:88-e5-v2]\r\n[quete_finie:88]Vive la résistance !![/quete_finie:88]\r\n*****\r\n<em>Quelques secondes passent et soudain, vous vous rappelez certains des gestes analysés dans le traité de Zin Ormil pouvant convenir au rang auquel vous semblez appartenir...</em> \r\n\r\n[ID:2](coups de pattes sur la toison écaillée) = (comment donc ?! pour qui vous prenez vous ?!)\r\n[/ID:2]\r\n*****\r\n(coup de queue vers le bas) + (léchage de babine) + (regard sous x) = (Hey me chauffe pas toi là ! Tu sais très bien qui je suis... et d''ailleurs avant que tu ne partes chez les autres, on avait quelque comptes à régler il me semble non ?)\r\n\r\n[ID:3](clignotement intense des micro écailles frontales) + (coup de queue pointue en direction du ciel) + (zig zigzag interrogatives) = (non, vous devez surement me confondre avec quelqu''un d''autre ! mais pourquoi êtes vous si agressif ?)[/ID:3]\r\n*****\r\n<em>Le maraudeur vous regarde un instant puis se tourne vers un autre maraudeur, échange quelques signes que vous ne comprenez pas, et revient vers vous...</em>\r\n\r\n|MAXI COMBO| : |sourcil droit blanc + coup de patte en avant + gratte gratte en arrière| + |spiral jetty rectangulaire| + |écailles vert Véronèse| + |wikikesalys| = |Hum ? peux être me suis-je trompé ? Alors tiens donc, tu ne me connais pas ? Eh bien je suis Milos Rekiwikys, ancien porte parole Kesalys du temps ou Rosa Bonheur était encore proche du pouvoir ! Ton stupide de Bartak a encouragé l’élection d''Azdak en suivant les indications de ces conseillers venus d''on ne sait quelle contrée. Mais tu sais tout cela et pourtant maintenant tu suis ses ordres; tu continues à œuvrer pour la guerre avec les autres, en vue de quoi ? ! peux tu me le dire ?|\r\n\r\n[ID:4](caresse dans le vide + retournement convulsif des poils de crinières laissant apparaitre le cuir du déguisement) + (coup de patte x étirement) = (On se calme ! Je n''espionne pas pour le Général Bartak, je suis à mon compte et les autres (les renégats) ne sont pas mes ennemis. j’essaie de comprendre la situation et j’œuvre pour la paix)[/ID:4]\r\n*****\r\n<em>Le maraudeur échange encore quelques signes que vous ne comprenez pas avec un autre maraudeur, puis s''adresse à vous oralement :</em>\r\n\r\nParlez à voix basse et faites semblant de caresser le vide au niveau de la ceinture. Vous n''êtes pas un maraudeur, je le savais. Le déguisement de ces ombres qui surveillent est incroyablement bien confectionné. L''imitation des écailles et de leur reflets multiples est une merveille...\r\n\r\nC''est Zin Ormil qui vous envoie ? \r\n\r\n[ID:5]En effet, oui c''est bien ce cher Zino, mais dites, comment connaissez vous notre langue ?[/ID:5]\r\n*****\r\nJ''ai connu un temps Zin Ormil. Nous échangions souvent autrefois sur nos cultures respectives... Il était comme l''un des nôtres jusqu''à ce que Rosa Bonheur ne disparaisse et que les rennes du pouvoir ne changent de mains. \r\n\r\n[ID:6]A quoi ressemblait cette Rosa Bonheur ?[/ID:6]\r\n*****\r\nC''était une très jolie maraudeuse, munie d''une robe bleu foncée. Elle semblait venir du même endroit que les autres conseillers mais elle avait des idées différentes... Elle méprisait la guerre et essayait de transmettre toutes les connaissances qu''elle avait... Et ça, elle en avait un paquet...\r\n\r\n[ID:7]Attendez, il se pourrait qu''elle ne soit pas forcément morte...\r\n<em>En murmurant :</em> A travers une fissure très étroite située en hauteur dans un des murs de la cellule numéro 1, nous avons pu observer de très loin, une maraudeuse toute de bleu vêtue allongée dans le sable...[/ID:7]\r\n*****\r\nComme ça ? !\r\n\r\n<em>Reprenant ses esprits, le maraudeur jette un rapide coup d''oeil à son compagnon avant de revenir vers vous en disant à voix basse :</em>\r\n\r\nOfficiellement, Rosa Bonheur s''est suicidée en se jetant d''un pont. Mais son corps n''a jamais été retrouvé.\r\n\r\nJe ne comprends pas vraiment pourquoi ils auraient accepté de la laisser en vie mais si ce que vous dites est vrai, cela nous donne un espoir de remettre en cause la dictature du Gouverneur Azdak.\r\n\r\n<em>Non loin de vous, des maraudeurs commencent à vous regarder avec insistance...</em>\r\n\r\nSi vous désirez vraiment nous aider, il vous faut voir Azdak. Profitez de votre rang et essayez de fouiller, de glaner quelques informations qui pourraient nous permettre de faire évader Rosa bonheur. Il y a des salles qui sont inaccessibles, des quartiers privés comme ceux du gouverneur ou des conseillers que vous pouvez observer, si vous montez sur ces caisses. \r\n\r\nFouinez tant que vous pouvez. Nous vous soutiendront en cas de problème.\r\n\r\nMaintenant saluez moi selon les usages Kesalys, et retirez vous, on nous observe.\r\n\r\n[ID:8]Oui je comprends...\r\n\r\n(gratte gratte) + (regard circulaire) = (merci et à une prochaine)[/ID:8]\r\n*****\r\n[quete:2]<em>Vous acceptez de collaborer avec Milos Rekiwikys afin de faire éclater le Parti communiste Kesalys</em>\r\n' WHERE `pnj`.`id` = 52;
UPDATE `pnj` SET `texte` = '(gratte gratte) = (bonjour)\r\n\r\n[ISQUETE:88-e5-v2](courbe queue + muqueuse terre de sienne) + (regard x y) + (coup de griffe alterné de pétillement argenté) = (Si vous rodez dans l''étage inférieur vous rencontrerez peut être Bazilio Véronèse, un maraudeur d''élite ayant subit des mutations végétales. Si mes informations sont bonnes, il devrait être dans l''une des salles aux canalisations, vers les 60 431. Il est impératif de le tuer. Ce fut l''un des colporteurs de mensonge à l''égard de Rosa Bonheur. Il a prit du galon et maintenant il surveille les troupes d''élites et les canalisations. Faites attention, il est devenu très dangereux)[/ISQUETE:88-e5-v2]' WHERE `pnj`.`id` = 53;

