
-- Table `quete`
DROP TABLE IF EXISTS `quete`;
CREATE TABLE IF NOT EXISTS `quete` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `nom` varchar(100) NOT NULL,
  `fournisseur` enum('bureau_quete','ecole_combat','taverne','alchimiste','université','bar','royaume','special') NOT NULL DEFAULT 'special',
  `type` enum('individuel','groupe','royaume') NOT NULL DEFAULT 'individuel',
  `repetable` enum('oui','non') NOT NULL DEFAULT 'non',
  `royaume` varchar(100) NOT NULL,
  `requis` text NOT NULL,
  `star_royaume` int(10) unsigned NOT NULL DEFAULT '0',
  `nombre_etape` int(10) unsigned NOT NULL DEFAULT '1',
  `terrain` varchar(10) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=151 ;



-- Table `quete_etape`
DROP TABLE IF EXISTS `quete_etape`;
CREATE TABLE `quete_etape` (
  `id` int(100) NOT NULL AUTO_INCREMENT,
  `id_quete` int(10) NOT NULL,
  `etape` int(10) NOT NULL DEFAULT '1',
  `variante` int(11) DEFAULT '0',
  `titre` varchar(50) NOT NULL,
  `description` text NOT NULL,
  `niveau` int(10) NOT NULL DEFAULT '1',
  `objectif` text NOT NULL,
  `collaboration` enum('aucune','groupe','royaume') NOT NULL DEFAULT 'groupe',
  `requis` text NOT NULL,
  `gain_perso` text NOT NULL,
  `gain_groupe` text NOT NULL,
  PRIMARY KEY (`id`),
  KEY `id` (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=142 ;



-- Table `quete_perso`
DROP TABLE IF EXISTS `quete_perso`;
CREATE TABLE `quete_perso` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `id_perso` int(10) unsigned NOT NULL,
  `id_quete` int(10) unsigned NOT NULL,
  `id_etape` int(10) unsigned NOT NULL,
  `avancement` text CHARACTER SET utf8 NOT NULL,
  PRIMARY KEY (`id`),
  KEY `perso_quete` (`id_perso`,`id_quete`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=8 ;
